import{_ as i}from"./slogan.b6573410.js";import{_ as a}from"./plugin-vue_export-helper.21dcd24c.js";import{e as s}from"./app.040d9bb0.js";const t={},e=s('<h1 id="prologas" tabindex="-1"><a class="header-anchor" href="#prologas" aria-hidden="true">#</a> Prologas</h1><p>\u0160iandien internetas primena ligot\u0105 feodalin\u0119 dinastij\u0105, kurioje oligarchai baigia aneksuoti \u017Eem\u0119, monopolizuoja \u017Emoni\u0173 sukurtus duomenis ir gauna \u017Eem\u0117s rent\u0105.</p><p>Manipuliuojama informacijos r\u016B\u0161iavimu, u\u017Erakinami socialiniai santykiai. Platformos yra meta-visatos dievai, gyvenantys ir mir\u0161tantys, o tie, kurie jomis abejoja, yra pa\u017Eeminami ir u\u017Edraud\u017Eiami.</p><p>Kaip \u017Euvys ant priekalo. Man nepatinka \u0161is pasaulis ir a\u0161 noriu siekti poky\u010Di\u0173.</p><p>Noriu atkurti nauj\u0105 internet\u0105: <strong>leisti kiekvienam</strong> tur\u0117ti savo duomenis. <strong>Tegul \u017Emoni\u0173 duomenys priklauso \u017Emon\u0117ms.</strong></p><p>Taigi \u0161iandien pradedu \u0161\u012F projekt\u0105 - rmw.link.</p><p><img src="'+i+'" alt="rmw.link\u6807\u8BED\u56FE"></p><h2 id="kaip-sukurti-visiems-vienoda-interneta" tabindex="-1"><a class="header-anchor" href="#kaip-sukurti-visiems-vienoda-interneta" aria-hidden="true">#</a> Kaip sukurti visiems vienod\u0105 internet\u0105?</h2><p>\u0160iandien internetas sukurtas pagal kliento ir serverio architekt\u016Br\u0105.</p><p>Klientas \u012Fkelia turin\u012F \u012F server\u012F, o serveris paskirsto duomenis \u017Ei\u016Brovui.</p><p>Kliento ir serverio architekt\u016Broje dviej\u0173 \u0161ali\u0173 galios n\u0117ra lygios.</p><p>Visi duomenys priklauso serveriui, o klientas gali tik pra\u0161yti serverio leidimo, kad b\u016Bt\u0173 matomas.</p><p>\u0160i nelogi\u0161ka architekt\u016Bra yra \u0161iandieninio oligarchinio interneto pasaulio pagrindas.</p><p>Norint visa tai pakeisti, reikia tiesiog m\u0105styti kitaip.</p><p>Kiekvienas asmuo saugo savo sukurtus duomenis ir platina juos savo abonentams.</p><p>Abonentai taip pat gal\u0117t\u0173 platinti duomenis vieni kitiems, tod\u0117l nereik\u0117t\u0173 serveri\u0173.</p><p>Taip pasiekiamas duomen\u0173 i\u0161lyginimas. Kiekvienas asmuo turi savo duomen\u0173 suverenum\u0105.</p><p>Remiantis turinio saugojimo ir platinimo decentralizavimu, galima toliau pl\u0117toti decentralizuot\u0105 kredit\u0173 sistem\u0105.</p><p>Kiekvienas asmuo gali i\u0161sinuomoti saugykl\u0105 ir pralaidum\u0105, saugodamas nenaudojamus i\u0161teklius mainais \u012F kito asmens saugyklos pralaidum\u0105 (kai jis nenaudojamas, vadinamas kreditu).</p><p>Pavyzd\u017Eiui, galite sukurti savo u\u017E\u0161ifruot\u0173 fail\u0173 atsargin\u0119 kopij\u0105 kito asmens kompiuteryje.</p><p>U\u017E saugykl\u0105 ir duomen\u0173 srauto pralaidum\u0105 svetain\u0117s administratoriui galima mok\u0117ti abonentin\u012F mokest\u012F.</p><p>Savo ruo\u017Etu tinklalapio valdytojas d\u0117l gerb\u0117j\u0173 prenumeratos turi daug duomen\u0173 saugyklos ir pralaidumo, kuriuos gali perparduoti, kad u\u017Edirbt\u0173 pinig\u0173 ir i\u0161laikyt\u0173 savo \u0161eim\u0105.</p><p>Mano nuomone, daugyb\u0117 blok\u0173 grandin\u0117s virtuali\u0173 monet\u0173, kurios sunaudoja daug i\u0161tekli\u0173 beprasmiams hashams apskai\u010Diuoti, gali b\u016Bti tik elektros energijos \u0161vaistymas ir n\u0117ra draugi\u0161kos aplinkai.</p><p>\u017Dinoma, &quot;Bitcoin&quot; turi savo istorin\u0119 reik\u0161m\u0119.</p><p>Tai buvo pirmas kartas, kai pasaulis suprato, kad vis dar egzistuoja programos, kad tik\u0117jimas turi kain\u0105 ir kad a\u0161 tikiu tvirtu savimi.</p><p>Tai buvo pirmas kartas, kai finans\u0173 sistema \u012Fsiliejo \u012F skaitmenin\u0119 visat\u0105, kaip Promet\u0117jas, pavog\u0119s dangaus ugn\u012F \u012F \u017Eem\u0119.</p><p>\u017Dod\u017Eio ugnis niekada neu\u017Eges, o civilizacija visada bus perduodama toliau. Bitcoin yra nauja \u017Emonijos skaitmenin\u0117s civilizacijos istorijos prad\u017Eia, o ne pabaiga.</p><p>Manau, kad juos pakeis skaitmeniniai atitikmenys, turintys didesn\u0119 praktin\u0119 vert\u0119 (pralaidumas, saugykla), ir bus sukurta visi\u0161kai nauja skaitmeninio pasaulio kreditini\u0173 pinig\u0173 sistema.</p><p>Pavyzd\u017Eiui, grup\u0117s ilgalaiki\u0173, nuolat veikian\u010Di\u0173 internetini\u0173 ind\u0117li\u0173 mazg\u0173 kreditai gal\u0117t\u0173 sudaryti krep\u0161elio valiut\u0105, kuri tarnaut\u0173 kaip universalus virtualaus pasaulio atitikmuo, vadinamas &quot;rmw.link moneta&quot;.</p><p>Suk\u016Brus tam tikr\u0105 masto ekonomij\u0105, galima netgi pritraukti l\u0117\u0161\u0173 kreditams teikti ir dividend\u0173 kreditams teikti.</p><p>Pasinaudojant kreditais ir dividendais, dalijimosi ekonomika gali b\u016Bti skatinama vystytis ir augti, didinant nenaudojam\u0173 i\u0161tekli\u0173 naudojim\u0105 ir skatinant \u017Emoni\u0173 iniciatyv\u0105 kurti vert\u0119.</p><p>\u017Dinoma, norint visa tai \u012Fgyvendinti, teks susidurti su daugybe technini\u0173 sunkum\u0173 ir kli\u016B\u010Di\u0173.</p><p>Bet a\u0161 tikiu: kur yra noras, ten yra ir kelias.</p><p>T\u016Bkstan\u010Dio myli\u0173 kelion\u0117 prasideda nuo pirmojo \u017Eingsnio.</p><p>Kaip Imaho ra\u0161\u0117 &quot;Legendoje apie Goku</p><blockquote><p>Noriu, kad dangus nebeu\u017Edengt\u0173 mano aki\u0173.</p><p>Noriu, kad \u017Eem\u0117 daugiau nebeguldyt\u0173 mano \u0161irdies.</p><p>Noriu, kad visos b\u016Btyb\u0117s suprast\u0173 mano vali\u0105.</p><p>Noriu, kad visi Budos i\u0161nykt\u0173 ore!</p></blockquote><p>Kaip sak\u0117 Paulas Ker\u010Dakinas.</p><blockquote><p>Brangiausias dalykas gyvenime yra gyvenimas, o kiekvienas \u017Emogus gyvena tik vien\u0105 kart\u0105.</p><p>\u017Dmogaus gyvenimas turi b\u016Bti praleistas taip, kad, atsigr\u0119\u017E\u0119s atgal, jis nesigail\u0117t\u0173, jog j\u012F i\u0161\u0161vaist\u0117, ir nesig\u0117dint\u0173, kad nieko nepadar\u0117.</p><p>Kai jis mirs, gal\u0117s pasakyti: &quot;Vis\u0105 savo gyvenim\u0105 ir visas j\u0117gas skyriau brangiausiam tikslui - kovai u\u017E visos \u017Emonijos i\u0161laisvinim\u0105.</p></blockquote><p>Kartu apverskime senus laikus auk\u0161tyn kojomis!</p><p><img src="//irmw.gumlet.io/1.jpg" alt=""></p><p><a href="https://rmw.zulipchat.com" target="_blank" rel="noopener noreferrer">Spustel\u0117kite \u010Dia, jei norite prisijungti prie &quot;zulipchat&quot; pokalbi\u0173 kambario</a> (rekomenduojame \u012Fsidiegti darbalaukio ir mobili\u0173j\u0173 telefon\u0173 klient\u0105, kad b\u016Bt\u0173 lengviau bendrauti realiuoju laiku) <sup class="footnote-ref"><a href="#fn1" id="fnref1">[1]</a></sup>.</p><hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p>zulipchat yra atviro kodo ir palaiko duomen\u0173 eksport\u0105 b\u016Bsimam perk\u0117limui \u012F rmw.link pagrindu pokalbi\u0173 platform\u0105. <a href="#fnref1" class="footnote-backref">\u21A9\uFE0E</a></p></li></ol></section>',43);function o(n,r){return e}var m=a(t,[["render",o],["__file","index.html.vue"]]);export{m as default};
