import{_ as n,r,o as t,c as s,a as e,b as p,F as l,e as o,d as a}from"./app.6835b22f.js";import{_ as v}from"./slogan.b6573410.js";const d={},j=o('<h1 id="prolog" tabindex="-1"><a class="header-anchor" href="#prolog" aria-hidden="true">#</a> Prolog</h1><p>Danes je internet podoben bolni fevdalni dinastiji, v kateri oligarhi dokon\u010Dujejo priklju\u010Ditev zemlji\u0161\u010D, monopolizirajo podatke, ki jih ustvarjajo ljudje, in se okori\u0161\u010Dajo z zemlji\u0161kimi rentami.</p><p>Z razvr\u0161\u010Danjem informacij se manipulira, socialni odnosi so zaklenjeni. Platforme so bogovi meta-vesolja, ki \u017Eivijo in umirajo, tisti, ki jim oporekajo, pa so degradirani in prepovedani.</p><p>Kot riba na nakovalu mi ta svet ni v\u0161e\u010D in \u017Eelim si prizadevati za spremembo.</p><p>\u017Delim obnoviti nov internet: svet, <strong>v</strong> katerem si <strong>vsakdo</strong> lasti svoje podatke. <strong>Naj podatki ljudi pripadajo ljudem.</strong></p><p>Zato danes za\u010Denjam ta projekt - rmw.link.</p><p><img src="'+v+'" alt="rmw.link\u6807\u8BED\u56FE"></p><h2 id="kako-vzpostaviti-enakopraven-internet-za-vse" tabindex="-1"><a class="header-anchor" href="#kako-vzpostaviti-enakopraven-internet-za-vse" aria-hidden="true">#</a> Kako vzpostaviti enakopraven internet za vse?</h2><p>Internet je danes zasnovan na arhitekturi odjemalec-stre\u017Enik.</p><p>Odjemalec nalo\u017Ei vsebino v stre\u017Enik, stre\u017Enik pa podatke posreduje gledalcu.</p><p>V arhitekturi odjemalec-stre\u017Enik ni enakosti mo\u010Di med stranema.</p><p>Stre\u017Enik je lastnik vseh podatkov, odjemalec pa lahko od stre\u017Enika zahteva le dovoljenje, da postane viden.</p><p>Ta nelogi\u010Dna arhitektura je temelj dana\u0161njega oligarhi\u010Dnega sveta interneta.</p><p>Da bi vse to spremenili, je treba preprosto razmi\u0161ljati druga\u010De.</p><p>Vsaka oseba shranjuje podatke, ki jih ustvari, in jih posreduje svojim naro\u010Dnikom.</p><p>Naro\u010Dniki bi lahko podatke posredovali tudi drug drugemu, s \u010Dimer bi odpravili potrebo po stre\u017Enikih.</p><p>Na ta na\u010Din se dose\u017Ee izena\u010Ditev podatkov. Vsaka oseba ima svojo suverenost glede podatkov.</p><p>Na podlagi decentralizacije shranjevanja in distribucije vsebin je mogo\u010De nadalje razviti decentraliziran kreditni sistem.</p><p>Vsaka oseba lahko najame shrambo in pasovno \u0161irino, tako da shranjuje neuporabljene vire v zameno za pasovno \u0161irino shrambe druge osebe (ki se imenuje dobroimetje, \u010De ni uporabljena).</p><p>Svoje datoteke lahko na primer \u0161ifrirano varnostno kopirate v ra\u010Dunalnik nekoga drugega.</p><p>Podobno se lahko shranjevanje in pasovna \u0161irina pla\u010Data spletnemu skrbniku spletnega mesta kot naro\u010Dnina.</p><p>Spletni skrbnik pa ima zaradi naro\u010Dnin svojih obo\u017Eevalcev veliko prostora za shranjevanje in pasovne \u0161irine, ki ju lahko preprodaja naprej, da zaslu\u017Ei denar in nahrani svojo dru\u017Eino.</p><p>Menim, da so \u0161tevilni virtualni kovanci verige blokov, ki porabijo veliko virov za izra\u010Dun nesmiselnih hashov, lahko le potrata elektri\u010Dne energije in niso okolju prijazni.</p><p>Bitcoin ima seveda svoj zgodovinski pomen.</p><p>Takrat je svet prvi\u010D spoznal, da so \u0161e vedno mogo\u010Di programi, da ima vera svojo ceno in da verjamem v trdnega sebe.</p><p>Finan\u010Dni sistem je bil prvi\u010D vgrajen v digitalno vesolje, tako kot Prometej, ki je nebe\u0161ki ogenj ukradel na zemljo.</p><p>Ogenj besede ne bo nikoli ugasnil in civilizacija se bo vedno prena\u0161ala naprej. Bitcoin je nov za\u010Detek in ne konec zgodovine \u010Dlove\u0161ke digitalne civilizacije.</p><p>Verjamem, da jih bodo nadomestili digitalni ekvivalenti z ve\u010Djo prakti\u010Dno vrednostjo (pasovna \u0161irina, pomnilnik), ki bodo ustvarili povsem nov sistem kreditnega denarja za digitalni svet.</p><p>Na primer, kredit skupine dolgoro\u010Dno delujo\u010Dih spletnih vozli\u0161\u010D, ki stalno delujejo, bi lahko tvoril ko\u0161arico valut, ki bi slu\u017Eila kot univerzalni ekvivalent virtualnega sveta, imenovano &quot;rmw.link kovanec&quot;.</p><p>Ko se ustvari dolo\u010Dena ekonomija obsega, je mogo\u010De celo zbirati sredstva za dodeljevanje kreditov in dividende za dodeljevanje kreditov.</p><p>S krediti in dividendami je mogo\u010De spodbuditi razvoj in rast delitvenega gospodarstva, pove\u010Dati uporabo neizkori\u0161\u010Denih virov in spodbuditi \u010Dlove\u0161ko pobudo za ustvarjanje vrednosti.</p><p>Seveda bo pri tehni\u010Dnih podrobnostih za uresni\u010Ditev vsega tega veliko te\u017Eav in ovir.</p><p>Vendar verjamem: kjer je volja, je tudi pot.</p><p>Pot tiso\u010D milj se za\u010Dne s prvim korakom.</p><p>Tako kot je Imaho napisal v Legendi o Gokuju</p><blockquote><p>Ho\u010Dem, da mi nebo ne zakriva ve\u010D o\u010Di.</p><p>\u017Delim, da zemlja ne bi ve\u010D pokopavala mojega srca.</p><p>\u017Delim, da vsa bitja razumejo mojo voljo.</p><p>\u017Delim, da se vsi budi razblinijo v zrak!</p></blockquote><p>Kot je dejal Paul Ker\u010Dakin.</p><blockquote><p>Najdragocenej\u0161a stvar v \u017Eivljenju je \u017Eivljenje, ki je za vsakega \u010Dloveka le enkrat.</p><p>\u010Clovek mora \u017Eivljenje pre\u017Eiveti tako, da mu ob pogledu nazaj ne bo \u017Eal, da ga je zapravil, in da ga ne bo sram, da ni storil ni\u010Desar.</p><p>Ko bo umrl, bo lahko rekel: &quot;Vse svoje \u017Eivljenje in vso svojo energijo sem posvetil najdragocenej\u0161emu cilju - boju za osvoboditev vsega \u010Dlove\u0161tva.</p></blockquote><p>Skupaj obrnimo stare \u010Dase na glavo!</p><p><img src="//irmw.gumlet.io/1.jpg" alt=""></p>',40),k={href:"https://rmw.zulipchat.com",target:"_blank",rel:"noopener noreferrer"},m=a("Kliknite tukaj in se pridru\u017Eite klepetalnici zulipchat"),u=a(" (priporo\u010Damo namestitev odjemalca za namizne ra\u010Dunalnike in mobilne telefone, da boste la\u017Eje komunicirali v realnem \u010Dasu) "),c=e("sup",{class:"footnote-ref"},[e("a",{href:"#fn1",id:"fnref1"},"[1]")],-1),b=a("."),g=o('<hr class="footnotes-sep"><section class="footnotes"><ol class="footnotes-list"><li id="fn1" class="footnote-item"><p>zulipchat je odprta koda in podpira izvoz podatkov za prihodnjo migracijo na platformo za klepet, ki temelji na rmw.link. <a href="#fnref1" class="footnote-backref">\u21A9\uFE0E</a></p></li></ol></section>',2);function h(z,f){const i=r("ExternalLinkIcon");return t(),s(l,null,[j,e("p",null,[e("a",k,[m,p(i)]),u,c,b]),g],64)}var x=n(d,[["render",h],["__file","index.html.vue"]]);export{x as default};
